/* Token charts scripts with apache echarts */
/* First chart */
let chartDom = document.getElementById('chart1');
let myChart = echarts.init(chartDom);

/* First chart option object */
let option = {
    tooltip: {
        trigger: 'item',
        formatter: '{a} <br/>{b}',
        backgroundColor: 'rgba(33,33,33,1)',
        borderRadius: 0,
        padding: 10,
        textStyle: {
            color: '#fff',
            fontStyle: 'normal',
            fontWeight: 'normal',
            fontFamily: "'Poppins', sans-serif",
            fontSize: 12,
        },
    },
    calculable: true,
    itemStyle: {
        normal: {
            shadowBlur: 5,
            shadowColor: 'rgba(0, 0, 0, 0.5)',
        },
    },
    series: [
        {
            name: 'Primary allocation',
            type: 'pie',
            radius: ['10%', '80%'],
            center: ['50%', '50%'],
            roseType: 'area',
            color: ['#8d62cd', '#0095eb', '#df0866', '#f68968', '#926262'],
            label: {
                normal: {
                    fontFamily: "'Poppins', sans-serif",
                    fontSize: 12,
                    color: '#000',
                },
            },
            /* Dataset for chart */
            data: [
                { value: 5, name: 'IDO(1%)' },
                { value: 80, name: 'Public Launch(40%)' },
                { value: 25, name: 'Airdrop(5%)' },
                { value: 75, name: 'Technology , Team  and Marketing(39%)' },
                { value: 25, name: 'NFT(5%)' },
                { value: 50, name: 'Staking Rewards(10%)' },
            ],
        },
    ],
};

option && myChart.setOption(option);

/* Second chart */
let chartDom2 = document.getElementById('chart2');
let myChart2 = echarts.init(chartDom2);

/* Second chart options object */
let option2 = {
    tooltip: {
        trigger: 'item',
        formatter: '{a} <br/>{b}',
        backgroundColor: 'rgba(33,33,33,1)',
        borderRadius: 0,
        padding: 10,
        textStyle: {
            color: '#fff',
            fontStyle: 'normal',
            fontWeight: 'normal',
            fontFamily: "'Poppins', sans-serif",
            fontSize: 12,
        },
    },
    calculable: true,
    itemStyle: {
        normal: {
            shadowBlur: 5,
            shadowColor: 'rgba(0, 0, 0, 0.5)',
        },
    },
    series: [
        {
            name: 'Use of Proceed',
            type: 'pie',
            radius: ['10%', '80%'],
            center: ['50%', '50%'],
            roseType: 'radius',
            color: ['#07ffdf', '#f2ebce', '#f68968', '#df0866', '#0095eb', '#f2ebce'],
            label: {
                normal: {
                    fontFamily: "'Poppins', sans-serif",
                    fontSize: 12,
                    color: '#000',
                },
            },
            /* Dataset for chart */
            data: [
                { value: 45, name: 'Team and Cofounders(9%)' },
                { value: 25, name: 'Reserve and Fund(5%) ' },
                { value: 50, name: 'Branding and marketing(10%)' },
                { value: 25, name: 'Bounty and events(5%)' },
                { value: 50, name: 'research and development(10%)' },
            ].sort(function (a, b) {
                return a.value - b.value;
            }),
        },
    ],
};

option2 && myChart2.setOption(option2);

window.addEventListener('resize', () => {
    myChart.resize();
    myChart2.resize();
});
